 import React from "react";
 import { useLocation , Link } from "react-router-dom";
 import win from "./win.jpg"

 const Gamepage = () => {
    const location = useLocation();
    return (
        <div style={{backgroundImage: `url(${win})`, 
                     height: '100vh', 
                     backgroundSize: 'cover' , 
                     display: "flex",
                     flexDirection: "column",
                     alignItems: "center",
                     justifyContent: "center",
        }}>
            <h2 style={{fontSize:"50px" , color:"darkmagenta"}}>Congratulations {location.state.username} !!!</h2>
            <h2 style={{fontSize:"30px"}}>Your score is {location.state.score}</h2>
            <p style={{fontSize:"20px"}}>Good luck with your next game!</p>
            <Link to="/leaderboard" state={{ username: location.state.username , score: location.state.score}}>
                <button>View Leaderboard</button>
            </Link>
            <br />
            <Link to="/Game" state={{ username: location.state.username}}>
                <button>Play Again</button>
            </Link>
            
        </div>
    )
 }

 export default Gamepage;
