import React from "react";
import { useLocation , Link } from "react-router-dom";
import win from "./win.jpg"

const Gamepage = () => {
  const location = useLocation();
  return (
    <div style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundImage: `url(${win})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}>
        <div className="wintext-container">
          <h2>Congratulations {location.state.username} !!!</h2>
          <h2>Your score is {location.state.score}</h2>
          <p>Good luck with your next game!</p>
      </div>
      <div className="button-container">
        <Link to="/leaderboard" state={{ username: location.state.username , score: location.state.score}}>
          <button>View Leaderboard</button>
        </Link>
        <Link to="/Game" state={{ username: location.state.username}}>
          <button>Play Again</button>
        </Link>
      </div>
    </div>
  );
};

export default Gamepage;
