import React from "react";

const Tile = ({ id, color, matched, selected, onClick }) => {
  const style = {
    width: "50px",
    height: "50px",
    backgroundColor: matched ? "gray" : selected ? color : "white",
    border: "1px solid black",
    margin: "5px",
    cursor: matched || selected ? "default" : "pointer",
  };

  const handleClick = () => {
    if (!matched && !selected) {
      onClick(id);
    }
  };

  return <div style={style} onClick={handleClick}></div>;
};

export default Tile;
