import React, { useState, useEffect } from "react";
import Tile from "./Tile";

const GameBoard = () => {
  const [tiles, setTiles] = useState([
    { id: 1, color: "red" },
    { id: 2, color: "blue" },
    { id: 3, color: "green" },
    { id: 4, color: "olive" },
    { id: 5, color: "red" },
    { id: 6, color: "yellow" },
    { id: 7, color: "pink" },
    { id: 8, color: "pink" },
    { id: 9, color: "green" },
    { id: 10, color: "blue" },
    { id: 11, color: "olive" },
    { id: 12, color: "yellow" },
  ]);

  const [selectedTileIds, setSelectedTileIds] = useState([]);
  const [matchedTileIds, setMatchedTileIds] = useState([]);

  useEffect(() => {
    const checkForMatch = () => {
      const [id1, id2] = selectedTileIds;
      if (id1 && id2) {
        const tile1 = tiles.find((tile) => tile.id === id1);
        const tile2 = tiles.find((tile) => tile.id === id2);
        if (tile1.color === tile2.color) {
          setMatchedTileIds((prevMatchedTileIds) => [
            ...prevMatchedTileIds,
            id1,
            id2,
          ]);
        }
      }
    };
    checkForMatch();
  }, [selectedTileIds, tiles]);

//   const handleTileClick = (id) => {
//     if (matchedTileIds.includes(id)) {
//       return;
//     }

//     if (selectedTileIds.length === 2) {
//       return;
//     }

//     setSelectedTileIds((prevSelectedTileIds) => [...prevSelectedTileIds, id]);
//   };

const handleTileClick = (id) => {
    const clickedTile = tiles.find((tile) => tile.id === id);
  
    // Don't allow the user to select a tile that has already been matched or is currently selected
    if (clickedTile.matched || clickedTile.selected) {
      return;
    }
  
    // Update the clicked tile to be selected
    clickedTile.selected = true;
  
    // Update the tiles array
    const updatedTiles = tiles.map((tile) =>
      tile.id === id ? clickedTile : tile
    );
  
    // Set the state with the updated tiles
    setTiles(updatedTiles);
  
    // Check if there are two selected tiles
    const selectedTiles = updatedTiles.filter((tile) => tile.selected);
  
    if (selectedTiles.length === 2) {
      // Check if the two selected tiles match
      if (selectedTiles[0].color === selectedTiles[1].color) {
        // Update the tiles to be matched
        const matchedTiles = updatedTiles.map((tile) =>
          tile.selected ? { ...tile, matched: true, selected: false } : tile
        );
  
        // Set the state with the matched tiles
        setTiles(matchedTiles);
      } else {
        // If the tiles do not match, wait 3 seconds and then reset them
        setTimeout(() => {
          const unmatchedTiles = updatedTiles.map((tile) =>
            tile.selected ? { ...tile, selected: false } : tile
          );
          setTiles(unmatchedTiles);
        }, 3000);
      }
    }
  };
  

  const handleRestartClick = () => {
    setTiles((prevTiles) =>
      prevTiles.map((tile) => ({ ...tile, matched: false }))
    );
    setSelectedTileIds([]);
    setMatchedTileIds([]);
  };

  return (
    <div>
      <div className="game-board" style={{ display: "flex", flexWrap: "wrap" }}>
        {tiles.map(({ id, color }) => (
          <Tile
            key={id}
            id={id}
            color={color}
            matched={matchedTileIds.includes(id)}
            selected={selectedTileIds.includes(id)}
            onClick={() => handleTileClick(id)}
          />
        ))}
      </div>
      <button onClick={handleRestartClick}>Restart</button>
    </div>
  );
};

export default GameBoard;
