import React from "react";
//import "./Tile.css";

const Tile = ({ id, color, selected, matched, onClick }) => {
  const tileStyle = {
    backgroundColor: matched ? "gray" : selected ? color : "white",
    cursor: matched || selected ? "default" : "pointer"
  };

  return (
    <div
      className="tile"
      style={tileStyle}
      onClick={() => onClick(id)}
    ></div>
  );
};

export default Tile;
